import {FETCH_BEERS,GET_BEER} from '../actions/consts';
export default function(state=[],action){

  if(action.error){
    return []
  }
  switch (action.type) {
    case FETCH_BEERS:
      return action.payload.data
      break;

    default:
      return state
  }
}
